<?phprequire_once("objects/user.php");require_once("objects/school.php");function displayTopContributors(){	global $mockUsers;	$mockUsers->resetUniqueIndex();	$usersSortedByDonations = array();		for($i = 0; $i < 5; $i++)	{		$aUser = $mockUsers->getUniqueUser();		if(isset($aUser))			$usersSortedByDonations[$aUser->moneyDonated] = $aUser;	}	krsort($usersSortedByDonations);	foreach ($usersSortedByDonations as $user)	{		displayUser($user);	}	}function displayTopCompetitors(){	global $mockUsers;	$mockUsers->resetUniqueIndex();	$usersSortedByWins = array();		for($i = 0; $i < 5; $i++)	{		$aUser = $mockUsers->getUniqueUser();		if(isset($aUser))			$usersSortedByWins[$aUser->matchesWon] = $aUser;	}	krsort($usersSortedByWins);	foreach ($usersSortedByWins as $user)	{		displayUser($user);	}}function displayTopSchools(){	global $mockSchools;	$mockSchools->resetUniqueIndex();	$SchoolsSortedByDonations = array();		for($i = 0; $i < 5; $i++)	{		$aSchool = $mockSchools->getUniqueSchool();		if(isset($aSchool))			$schoolsSortedByDonations[$aSchool->moneyDonated] = $aSchool;	}	krsort($schoolsSortedByDonations);	foreach ($schoolsSortedByDonations as $school)	{		displaySchool($school);	}}function displayUser($user){?>	<hr />	<div class="media">	  <div class="pull-left" style="position:relative; width:64px; height:64px; overflow:hidden;">		  <a href="#">			<img src ="<?echo($user->pictureUrl);?>" width="64" height="64" />		  </a>	  </div>	  	   <a class="pull-right" href="#">		<h4>Challenge</h4>	   </a>	  	  <div class="media-body">			<strong><?echo($user->name);?></strong><br />			Money Donated: <strong style="color:red;">$<? echo($user->moneyDonated); ?> </strong><br />			Matches Won:   <strong style="color:red;"><? echo($user->matchesWon); ?> </strong><br />			Home School: <? echo($user->homeSchool); ?>							<div class="media">		   <a href="#"> Message</a> |		   <a href="#"> View Current Challenges</a>		</div>	  </div>	</div><?}function displaySchool($school){?>	<hr />	<div class="media">	  <div class="pull-left" style="position:relative; width:64px; height:64px; overflow:hidden;">		  <a href="#">			<img src ="<?echo($school->pictureUrl);?>" width="64" height="64" />		  </a>	  </div>	  	   <a class="pull-right" href="#">			   </a>	  	  <div class="media-body">			<strong><?echo($school->name);?></strong><br />			Money Donated: <strong style="color:red;">$<? echo($school->moneyDonated); ?> </strong><br />							<div class="media">		   <a href="#"> View Schedule</a> |		   <a href="#"> View Current Challenges</a>		</div>	  </div>	</div><?}?>