<?phprequire_once($_SERVER["DOCUMENT_ROOT"] . "/widget/scoreboard.php");function showBanner($theGame){	$homeTeam = $theGame->get_home_team();	$awayTeam = $theGame->get_away_team();?>		<div class="row-fluid banner">			<div class="banner-homeTeam"><?print($homeTeam->get_name());?></div>			<div class="banner-awayTeam"><?print($awayTeam->get_name());?></div>						<img src="img/matchup_banner/title.jpg" alt="<?print($homeTeam->get_name() . " vs " . $awayTeam->get_name());?>"/>			<div class="row-fluid">				<div class="span6 banner-teams">					<img src="img/matchup_banner/split.png" alt="vs." class="banner-split"/>					<img src="img/teams/<?print($homeTeam->get_photo());?>" alt="<?print($homeTeam->get_name());?>"/>				</div>				<img class="span6" src="img/teams/<?print($awayTeam->get_photo());?>" alt="<?print($awayTeam->get_name());?>"/>			</div>		</div>		<div class="row-fluid" style="font-weight:bold; font-size:12px; background-color:#E3E3E3; padding-top: 10px;margin-bottom: 12px;">			<ul >				<!--<li class="text-info"><?print($homeTeam->get_name() . " vs " . $awayTeam->get_name());?><span class="divider"> </span></li>-->				<li class="span2">Date: <?echo($theGame->get_formatted_date());?><span class="divider"> </span></li>				<li class="span2">Tipoff: <?echo($theGame->get_formatted_time());?><span class="divider"> </span></li>				<li class="span4">At <?echo($theGame->get_stadium());?> Stadium<span class="divider"> </span></li>				<li class="span4"><?showScores($theGame);?></li>			</ul>			<div class="clearfix"></div>		</div><?}?>