<?phprequire_once($_SERVER["DOCUMENT_ROOT"] . "/structs/user.php");$sid = session_id();if(!isset($sid)) {    session_start();}if($_SERVER['REQUEST_METHOD'] == 'POST' && $_POST['action'] == "login") {	process_login();}function process_login(){	if($_POST['inputEmail'] != '' && $_POST['inputPassword'] != '')	{		$user = User::login($_POST['inputEmail'], $_POST['inputPassword']);				if(isset($user)) {			$_SESSION['me'] = $user;			show_logged_in_widget();					} else {			show_login_widget("Login Incorrect");		}	}else {		show_login_widget("Login Incorrect");	}}function show_login_widget($errors){	$banner = "<div class='alert alert-info' style='padding:6px 28px 6px 11px; margin-bottom:12px;'>Login Manually</div>";	if(!empty($errors)) {		$banner="<div class='alert alert-error' style='padding:6px 28px 6px 11px; margin-bottom:12px;'>$errors</div>";	}?>	<div class="well" style="margin-bottom:8px">		<?echo($banner);?>		<form id="login_form" class="" style="margin:0;" action="login.php" onsubmit="loginWidgetAttempt(); return false;">		  <div class="control-group">			<div class="controls input-prepend" style="margin-bottom:0px;">				<span class="add-on" style="width:68px;">Email:</span>			  <input type="text" class="span8" name="inputEmail" id="inputEmail" />			</div>		  </div>		  <div class="control-group">			<div class="controls input-prepend" style="margin-bottom:0px;">				<span class="add-on" style="width:68px;">&nbsp;Password:</span>			  <input type="password" class="span8" name="inputPassword" id="inputPassword"/>			  <input type="hidden" name="action" id="action" value="login"/>			</div>		  </div>			<div class="row-fluid">				<div class="span6">					<button class="btn btn-primary btn-block" type="submit">Login</button>				</div>				<div class="span6">					<a href="#" data-toggle="tooltip" title="Facebook Only Right Now" class="btn btn-primary btn-block disabled">Register</a>				</div>			</div>		</form>	</div><?}function show_logged_in_widget(){	$banner = "<div class='alert alert-success'>Welcome Back!</div>";?>	<div class="well" style="margin-bottom:10px">		<?echo($banner);?>		<form id="login_form" class="" style="margin:0;" action="login.php" onsubmit="loginWidgetAttempt(); return false;">		  <div class="control-group">			<div class="controls input-prepend">				<span class="add-on span4">Email:</span>			  <input type="text" class="span8 disabled" name="inputEmail" id="inputEmail" />			</div>		  </div>		  <div class="control-group">			<div class="controls input-prepend">				<span class="add-on span4">Password:</span>			  <input type="password" class="span8 disabled" name="inputPassword" id="inputPassword"/>			  <input type="hidden" name="action" id="action" value="login"/>			</div>		  </div>			<div class="row-fluid">				<div class="span6">					<button class="btn btn-primary btn-block disabled" type="submit">Login</button>				</div>				<div class="span6">					<a href="register.php" class="btn btn-primary btn-block disabled">Register</a>				</div>			</div>		</form>	</div>		<script>		location.reload(false);	</script><?}?>		